import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // For navigation after login
import "./Login.css";
import korisnickoIme_icon from "../assets/korisnickoIme.png";
import lozinka_icon from "../assets/lozinka.png";
import pozadina from "../assets/pozadina.png";
import axios from "axios";

function Login() {
    const navigate = useNavigate(); // Initialize navigation
    const [loginForm, setLoginForm] = useState({
        korisnickoIme: "",
        lozinka: "",
    });
    const [error, setError] = useState("");

    // Check if form is valid
    function isValid() {
        const { korisnickoIme, lozinka } = loginForm;
        return korisnickoIme.length > 0 && lozinka.length > 0;
    }

    // Update form data on input change
    function onChange(event) {
        const { name, value } = event.target;
        setLoginForm((oldForm) => ({ ...oldForm, [name]: value }));
    }

    // Handle login
    const handleLogin = async (e) => {
        e.preventDefault();

        const payload = {
            korisnickoIme: loginForm.korisnickoIme,
            lozinka: loginForm.lozinka,
        };

        try {
            console.log(payload);
            const response = await axios.post("http://localhost:8080/api/admin/login", payload);
            console.log("Login successful:", response.data);

            // Navigate to admin dashboard on successful login
            navigate("/admin-dashboard");
        } catch (error) {
            console.error("Login failed:", error);
            setError("Neispravno korisničko ime ili lozinka. Molimo pokušajte ponovno.");
        }
    };

    return (
        <div className="loginpage">
            <img className="backgroundimg" src={pozadina} alt="Pozadina" />
            <div className="box">
                <form onSubmit={handleLogin}>
                    <div className="gore">
                        <div className="tekst">Prijava admina</div>
                        <div className="underline"></div>
                    </div>
                    <div className="inputs">
                        <div className="input">
                            <img src={korisnickoIme_icon} alt="Korisničko ime ikona" />
                            <input
                                name="korisnickoIme"
                                placeholder="Korisničko ime"
                                onChange={onChange}
                                value={loginForm.korisnickoIme}
                            />
                        </div>
                        <div className="input">
                            <img src={lozinka_icon} alt="Lozinka ikona" />
                            <input
                                name="lozinka"
                                type="password" // Correct input type for passwords
                                placeholder="Lozinka"
                                onChange={onChange}
                                value={loginForm.lozinka}
                            />
                        </div>
                    </div>
                    <div className="submit">
                        <button className="prijava" type="submit" disabled={!isValid()}>
                            Prijavi se
                        </button>
                    </div>
                </form>
                {error && <p className="error">{error}</p>}
            </div>
        </div>
    );
}

export default Login;
